library(nlmixr)
library(tdmore)
library(testthat)
library(RxODE)
library(dplyr)

expect_not_equal <- function(a, b) {
  expect_false(isTRUE(all.equal(a,b)))
}

context("Test the predict function (on both tdmore and tdmorefit objects)")

m1 <- getModel("fluticasone")
regimen <- data.frame(
  TIME=seq(0, by=24, length.out=10),
  AMT=500 # 500ug standard dose
)

#The below was generated by:
#predict(m1, newdata=c(0, 2, 4), regimen=regimen) %>% write.table(file="", row.names=F)
expected_output <- '
"TIME" "Ka" "CL" "V1" "V2" "Q" "K12" "K21" "CONC" "center" "periph"
0 3.87 659 56900 5550 259 0.0045518453427065 0.0466666666666667 8.78734622144113 500 0
2 3.87 659 56900 5550 259 0.0045518453427065 0.0466666666666667 8.51187193643804 484.325513183325 4.27604859651819
4 3.87 659 56900 5550 259 0.0045518453427065 0.0466666666666667 8.25162248766285 469.517319548016 8.03869893509187
' %>% read.table(text=., header=T, row.names=c())

test_that("predict.tdmore() single profiles", {
  #you need to specify `newdata`
  expect_error( predict(m1) )
  #empty newdata should still give all output columns
  expect_equal( predict(m1, newdata=c()), subset(expected_output, FALSE))
  #no treatment regimen
  expect_equal( predict(m1, newdata=c(0, 2)),
                expected_output %>% filter(TIME %in% c(0,2)) %>% mutate(CONC=0, center=0, periph=0) )
  #with treatment regimen
  expect_equal( predict(m1, newdata=c(0, 2), regimen),
                expected_output %>% filter(TIME %in% c(0,2)) )
  #everything
  expect_equal( predict(m1, newdata=c(0, 2, 4), regimen),
                expected_output )

  # only the TIME column
  expect_equal( predict(m1, newdata=data.frame(TIME=c(0, 2, 4)), regimen),
                expected_output %>% select(TIME) )
  # only the TIME and CONC column
  expect_equal( predict(m1, newdata=data.frame(TIME=c(0, 2, 4), CONC=NA), regimen),
                expected_output %>% select(TIME, CONC) )

  # If values in CONC, they are overwritten
  expect_equal( predict(m1, newdata=data.frame(TIME=c(0, 2, 4), CONC=runif(3)), regimen),
                expected_output %>% select(TIME, CONC) )

  # Also possible to ask for model outputs without any residual error model attached
  expect_equal( predict(m1, newdata=data.frame(TIME=c(0, 2, 4), periph=runif(3)), regimen),
                expected_output %>% select(TIME, periph) )

  predict(m1, parameters=c(ECL=1),
          newdata=data.frame(TIME=c(0, 2, 4), periph=runif(3)),
          regimen)
})

